import React, { useState } from "react";
import Menu from "./Menu";
import Categories from "./Categories";
import items from "./data";

const allCategories = ["Tất cả", ...new Set(items.map((item) => item.category))];
//console.log(allCategories);

function App() {
  const [menuItems, setMenuItems] = useState(items);
  const [category, setCategory] = useState(allCategories);

  //console.log(menuItems);
  const filterItems = (cate) => {
    if (cate === "Tất cả") {
      setMenuItems(items);
      return;
    }

    const newItems = items.filter((item) => item.category === cate);
    setMenuItems(newItems);
  };

  return (
    <main>
      <div className="title">
        <h2>Cửa hàng cây cảnh T&H</h2>
        <div className="underline"></div>
      </div>
      <Categories filterItems={filterItems} category={category} />
      <Menu menuItems={menuItems} />
    </main>
  );
}

export default App;
